import React from 'react';
import TransactionForm from './transactionForm';
import BarangList from "../barang/BarangList";

const User = ({ user, transactions, availableItems, onTransactionSubmit, onLogout, barangs }) => {
  const handleTransactionSubmit = (transactionData) => {
    onTransactionSubmit(transactionData);
  };

  return (
    <div>
      <h2>User Page</h2>
      <p>Welcome to the user page, {user}.</p>
      <TransactionForm availableItems={availableItems} onTransactionSubmit={handleTransactionSubmit} />
      <h3>Transaction History</h3>
      <ul>
        {transactions.map((transaction) => (
          <li key={transaction.id}>
            {transaction.itemName} - {transaction.quantity} - {transaction.status}
          </li>
        ))}
      </ul>
      <h3>Available Items</h3>
      {barangs.length > 0 ? (
        <BarangList barangs={barangs} onBuy={(itemId) => console.log("Buy item with id", itemId)} />
      ) : (
        <div>No items available</div>
      )}
      <button className="btn btn-secondary mt-3" onClick={onLogout}>Logout</button>
    </div>
  );
  
};

export default User;
