import React, { useState } from 'react';
import PropTypes from 'prop-types';

const AddBarang = ({ onAdd, onCancel }) => {
  const [namaBarang, setNamaBarang] = useState('');
  const [jenisBarang, setJenisBarang] = useState('');
  const [hargaBarang, setHargaBarang] = useState('');
  const [stokBarang, setStokBarang] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newBarang = {
      id: Date.now(),
      namaBarang: namaBarang || 'Default Nama Barang',
      jenisBarang: jenisBarang || 'Default Jenis Barang',
      hargaBarang: hargaBarang || 'Default Harga Barang',
      stokBarang: stokBarang || 'Default Stok Barang',
    };
    onAdd(newBarang);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Add New Barang</h2>
      <form onSubmit={handleSubmit}>
        <div className="mb-3">
          <label className="form-label">Nama Barang</label>
          <input
            type="text"
            className="form-control"
            value={namaBarang}
            onChange={(e) => setNamaBarang(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Jenis Barang</label>
          <input
            type="text"
            className="form-control"
            value={jenisBarang}
            onChange={(e) => setJenisBarang(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Harga Barang</label>
          <input
            type="number"
            className="form-control"
            value={hargaBarang}
            onChange={(e) => setHargaBarang(e.target.value)}
            required
          />
        </div>
        <div className="mb-3">
          <label className="form-label">Stok Barang</label>
          <input
            type="number"
            className="form-control"
            value={stokBarang}
            onChange={(e) => setStokBarang(e.target.value)}
            required
          />
        </div>
        <button type="submit" className="btn btn-success">Add Barang</button>
        <button type="button" className="btn btn-secondary ms-2" onClick={onCancel}>Cancel</button>
      </form>
    </div>
  );
};

AddBarang.propTypes = {
  onAdd: PropTypes.func.isRequired,
  onCancel: PropTypes.func.isRequired,
};

export default AddBarang;
