import React, { useState } from 'react';

const KasirPage = ({ transactions, barangs, onValidateTransaction, onRequestSupplier }) => {
  const [payment, setPayment] = useState('');
  const [discount, setDiscount] = useState('');
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [stockAvailable, setStockAvailable] = useState(true);

  const handleSelectTransaction = (transaction) => {
    setSelectedTransaction(transaction);
    const barang = barangs.find(b => b.id === transaction.barangId);
    if (barang && barang.stock < transaction.quantity) {
      setStockAvailable(false);
      onRequestSupplier(barang.id, transaction.quantity - barang.stock);
    } else {
      setStockAvailable(true);
    }
    setPayment('');
    setDiscount('');
  };

  const handleValidationSubmit = (e) => {
    e.preventDefault();
    if (stockAvailable) {
      onValidateTransaction(selectedTransaction.id, payment, discount);
    } else {
      alert('Stock not available. Request has been sent to supplier.');
    }
    setSelectedTransaction(null);
    setPayment('');
    setDiscount('');
  };

  return (
    <div>
      <h2>Kasir Page</h2>
      <h3>Pending Transactions</h3>
      <ul className="list-group">
        {transactions
          .filter(transaction => transaction.status === 'ongoing')
          .map(transaction => (
            <li
              key={transaction.id}
              className="list-group-item"
              onClick={() => handleSelectTransaction(transaction)}
            >
              {transaction.itemName} - {transaction.quantity} @ {transaction.price}
            </li>
          ))}
      </ul>
      {selectedTransaction && (
        <form onSubmit={handleValidationSubmit} className="mt-3">
          <h4>Validate Transaction</h4>
          <div className="mb-3">
            <label htmlFor="payment" className="form-label">Payment</label>
            <input
              type="number"
              className="form-control"
              id="payment"
              value={payment}
              onChange={(e) => setPayment(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="discount" className="form-label">Discount</label>
            <input
              type="number"
              className="form-control"
              id="discount"
              value={discount}
              onChange={(e) => setDiscount(e.target.value)}
            />
          </div>
          <button type="submit" className="btn btn-primary">Validate</button>
        </form>
      )}

      <h3>Transaction History</h3>
      <ul className="list-group mt-3">
        {transactions
          .filter(transaction => transaction.status === 'confirmed')
          .map(transaction => (
            <li key={transaction.id} className="list-group-item">
              {transaction.itemName} - {transaction.quantity} @ {transaction.price} - Payment: {transaction.payment} - Discount: {transaction.discount}
            </li>
          ))}
      </ul>
    </div>
  );
};

export default KasirPage;
