import React, { useState } from 'react';

function AdminPage({ barangs, updateStock }) {
  const [selectedBarang, setSelectedBarang] = useState(null);
  const [stockToAdd, setStockToAdd] = useState(0);
  const [errorMessage, setErrorMessage] = useState('');

  const handleStockChange = (event) => {
    const stock = parseInt(event.target.value, 10);
    if (stock >= 0) {
      setStockToAdd(stock);
      setErrorMessage('');
    } else {
      setErrorMessage('Stock must be a positive number.');
    }
  };

  const handleAddStock = () => {
    if (selectedBarang && stockToAdd > 0) {
      updateStock(selectedBarang.id, stockToAdd);
      setStockToAdd(0);
    }
  };

  return (
    <div>
      <h2>Admin Page</h2>
      <h3>List of Items</h3>
      <ul>
        {barangs.map((barang) => (
          <li key={barang.id}>
            {barang.namaBarang} - {barang.stock} in stock
            {barang.stock === 0 && ' (Unavailable)'}
            <button onClick={() => setSelectedBarang(barang)}>Add Stock</button>
          </li>
        ))}
      </ul>
      {selectedBarang && (
        <div>
          <h3>Add Stock to {selectedBarang.namaBarang}</h3>
          <input
            type="number"
            value={stockToAdd}
            onChange={handleStockChange}
            min="1"
          />
          {errorMessage && <p className="text-danger">{errorMessage}</p>}
          <button onClick={handleAddStock}>Add Stock</button>
        </div>
      )}
    </div>
  );
}

export default AdminPage;
