import React, { useState } from 'react';

import User from './components/userfd/user';
import TransactionForm from './components/userfd/transactionForm';
import KasirPage from './components/key/KasirPage';
import SupplierPage from './components/key/SupplierPage';
import AdminPage from './components/key/AdminPage';
import BarangList from './components/barang/BarangList'; // Mengubah dari ItemList ke BarangList
import AddBarang from './components/barang/AddBarang';
import EditBarang from './components/barang/EditBarang';
import DeleteBarang from './components/barang/DeleteBarang';

// Import data (data yang digunakan sebelumnya)
import data from './data/barangs.json'; 

function App() {
  const [barangs, setBarangs] = useState(data.barangs);
  const [currentBarang, setCurrentBarang] = useState(null);
  const [showEditBarang, setShowEditBarang] = useState(false);
  const [showAddBarang, setShowAddBarang] = useState(false);
  const [showDeleteBarang, setShowDeleteBarang] = useState(false);
  const [barangToDelete, setBarangToDelete] = useState(null);
  const [isLogin, setIsLogin] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [user, setIsUser] = useState('');

  const handleDelete = (id) => {
    setBarangToDelete(id);
    setShowDeleteBarang(true);
  };

  const deleteBarang = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
    setShowDeleteBarang(false);
  };

  const handleCancelDelete = () => {
    setShowDeleteBarang(false);
  };

  const handleEdit = (id) => {
    const barang = barangs.find((barang) => barang.id === id);
    setCurrentBarang(barang);
    setShowEditBarang(true);
  };

  const editBarang = (updatedBarang) => {
    const updatedBarangs = barangs.map((barang) =>
      barang.id === updatedBarang.id ? updatedBarang : barang
    );
    setBarangs(updatedBarangs);
    setCurrentBarang(null);
    setShowEditBarang(false);
  };

  const handleCancelEdit = () => {
    setCurrentBarang(null);
    setShowEditBarang(false);
  };

  const handleAddBarang = () => {
    setShowAddBarang(true);
  };

  const addBarang = (barang) => {
    const newId = barangs.length > 0 ? Math.max(...barangs.map(b => b.id)) + 1 : 1;
    const newBarang = {
      id: newId,
      ...barang,
    };
    const updatedBarangs = [...barangs, newBarang];
    setBarangs(updatedBarangs);
    setShowAddBarang(false);
  };

  const handleCancelAdd = () => {
    setShowAddBarang(false);
  };

  const login = () => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      setIsUser('Udin');
      setIsLogin(true);
    }, 3000);
  };

  return (
    <div className="App">
      <h1>Barang</h1>
      {isLogin && showDeleteBarang ? (
        <DeleteBarang
          barang={barangs.find((barang) => barang.id === barangToDelete)}
          deleteBarang={deleteBarang}
          onCancel={handleCancelDelete}
        />
      ) : isLogin && showEditBarang ? (
        <EditBarang
          barang={currentBarang}
          editBarang={editBarang}
          onCancel={handleCancelEdit}
        />
      ) : isLogin && showAddBarang ? (
        <AddBarang addBarang={addBarang} onCancel={handleCancelAdd} />
      ) : isLogin ? (
        <>
          <BarangList
            barangs={barangs}
            onDelete={handleDelete}
            onEdit={handleEdit}
            onAdd={handleAddBarang}
            setIsLogin={setIsLogin}
            user={user}
          />
        </>
      ) : (
        <>
          Login
          <button onClick={login}>{isLoading ? 'loading...' : 'Login'}</button>
        </>
      )}
    </div>
  );
}

export default App;
