import React, { useState } from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';
import { Modal, Button, Table, Alert } from 'react-bootstrap';

const formatHarga = (harga) => {
  return `Rp. ${Number(harga).toLocaleString('id-ID')}`;
};

const User = ({ transactions = [], barangs = [], handleTransactionSubmit, onLogout }) => {
  const [selectedBarang, setSelectedBarang] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [showModal, setShowModal] = useState(false);
  const [showAlert, setShowAlert] = useState(false);

  const handleBarangSelect = (barang) => {
    setSelectedBarang(barang);
  };

  const handleQuantityChange = (e) => {
    setQuantity(parseInt(e.target.value));
  };

  const handleShowModal = () => {
    if (selectedBarang && quantity > 0) {
      setShowModal(true);
    } else {
      setShowAlert(true);
    }
  };

  const handleCloseModal = () => {
    setShowModal(false);
  };

  const handleSubmitOrder = () => {
    if (selectedBarang && quantity > 0) {
      const transaction = {
        id: transactions.length > 0 ? Math.max(...transactions.map(t => t.id)) + 1 : 1, // Assign unique ID
        itemName: selectedBarang.namaBarang,
        quantity: quantity,
        barangId: selectedBarang.id,
        price: selectedBarang.hargaBarang,
        status: 'pending'
      };
      handleTransactionSubmit(transaction);
      setSelectedBarang(null);
      setQuantity(1);
      setShowModal(false);
    }
  };

  return (
    <div className="container mt-4">
      {showAlert && (
        <Alert variant="danger" onClose={() => setShowAlert(false)} dismissible>
          Please select a barang and enter a valid quantity.
        </Alert>
      )}

      <div className="row">
        <div className="col-md-6 mb-4">
          <div className="card">
            <div className="card-header bg-primary text-white">Available Barangs</div>
            <div className="card-body p-0">
              <Table hover>
                <thead className="thead-dark">
                  <tr>
                    <th>ID</th>
                    <th>Nama Barang</th>
                    <th>Jenis Barang</th>
                    <th>Harga Barang</th>
                    <th>Stok Barang</th>
                  </tr>
                </thead>
                <tbody>
                  {barangs.map((barang) => (
                    <tr
                      key={barang.id}
                      className={selectedBarang && selectedBarang.id === barang.id ? 'table-primary' : ''}
                      onClick={() => handleBarangSelect(barang)}
                      style={{ cursor: 'pointer' }}
                    >
                      <td>{barang.id}</td>
                      <td>{barang.namaBarang}</td>
                      <td>{barang.jenisBarang}</td>
                      <td>{formatHarga(barang.hargaBarang)}</td>
                      <td>{barang.stokBarang}</td>
                    </tr>
                  ))}
                </tbody>
              </Table>
            </div>
          </div>
        </div>

        <div className="col-md-6 mb-4">
          {selectedBarang && (
            <div className="card">
              <div className="card-header bg-success text-white">Selected Barang</div>
              <div className="card-body">
                <h3>{selectedBarang.namaBarang}</h3>
                <div className="input-group mb-3">
                  <input
                    type="number"
                    min="1"
                    value={quantity}
                    onChange={handleQuantityChange}
                    className="form-control"
                    aria-label="Quantity"
                  />
                  <div className="input-group-append">
                    <Button onClick={handleShowModal} className="btn btn-success">
                      Order
                    </Button>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>

      <div className="card mb-4">
        <div className="card-header bg-info text-white">Transaction List</div>
        <div className="card-body p-0">
          <Table striped hover>
            <thead className="thead-dark">
              <tr>
                <th>ID</th>
                <th>Item Name</th>
                <th>Quantity</th>
                <th>Status</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((transaction) => (
                <tr key={transaction.id}>
                  <td>{transaction.id}</td>
                  <td>{transaction.itemName}</td>
                  <td>{transaction.quantity}</td>
                  <td>{transaction.status || 'pending'}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      </div>

      <Modal show={showModal} onHide={handleCloseModal}>
        <Modal.Header closeButton>
          <Modal.Title>Confirm Order</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>Are you sure you want to order <strong>{quantity}</strong> units of <strong>{selectedBarang?.namaBarang}</strong>?</p>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCloseModal}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSubmitOrder}>
            Confirm
          </Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
};

User.propTypes = {
  transactions: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      itemName: PropTypes.string.isRequired,
      quantity: PropTypes.number.isRequired,
      price: PropTypes.number,
      status: PropTypes.string,
    })
  ).isRequired,
  barangs: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      namaBarang: PropTypes.string.isRequired,
      jenisBarang: PropTypes.string.isRequired,
      hargaBarang: PropTypes.number.isRequired,
      stokBarang: PropTypes.number.isRequired,
    })
  ).isRequired,
  handleTransactionSubmit: PropTypes.func.isRequired,
  onLogout: PropTypes.func.isRequired,
};

export default User;
