import React, { useState, useEffect } from 'react';
import data from '../../data/barang.json'; // Pastikan path ke barang.json benar
import 'bootstrap/dist/css/bootstrap.min.css';

const formatHarga = (harga) => {
  return `Rp. ${Number(harga).toLocaleString('id-ID')}`;
};

function User({ handleTransactionSubmit, onLogout }) {
  const [barangs, setBarangs] = useState([]);
  const [selectedBarang, setSelectedBarang] = useState(null);
  const [quantity, setQuantity] = useState(1);

  useEffect(() => {
    setBarangs(data.barangs || []);
  }, []);

  const handleBarangSelect = (barang) => {
    setSelectedBarang(barang);
  };

  const handleQuantityChange = (e) => {
    setQuantity(parseInt(e.target.value));
  };

  const handleSubmitOrder = () => {
    if (selectedBarang && quantity > 0) {
      const transaction = {
        barangId: selectedBarang.id,
        quantity: quantity,
      };
      handleTransactionSubmit(transaction);
      setSelectedBarang(null);
      setQuantity(1);
    } else {
      alert("Please select a barang and enter a valid quantity.");
    }
  };

  return (
    <div className="container mt-4">
      <h1>User Page</h1>
      <div>
        <h2>Available Barangs</h2>
        <table className="table table-striped">
          <thead className="thead-dark">
            <tr>
              <th>ID</th>
              <th>Nama Barang</th>
              <th>Jenis Barang</th>
              <th>Harga Barang</th>
              <th>Stok Barang</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {barangs.map((barang) => (
              <tr key={barang.id} onClick={() => handleBarangSelect(barang)}>
                <td>{barang.id}</td>
                <td>{barang.namaBarang}</td>
                <td>{barang.jenisBarang}</td>
                <td>{formatHarga(barang.hargaBarang)}</td>
                <td>{barang.stokBarang}</td>
                <td>
                  <button
                    className="btn btn-success"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleBarangSelect(barang);
                    }}
                  >
                    Beli
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div className="mt-4">
        {selectedBarang && (
          <div>
            <h3>Selected Barang: {selectedBarang.namaBarang}</h3>
            <input
              type="number"
              min="1"
              value={quantity}
              onChange={handleQuantityChange}
            />
            <button className="btn btn-primary" onClick={handleSubmitOrder}>Order</button>
          </div>
        )}
      </div>
      <button className="btn btn-secondary mt-3" onClick={onLogout}>
        Logout
      </button>
    </div>
  );
}

export default User;
