// File: ./components/barang/AddBarang.jsx
import React, { useState } from 'react';

function AddBarang({ onAdd, onCancel }) {
  const [namaBarang, setNamaBarang] = useState('');
  const [stokBarang, setStokBarang] = useState(0);
  const [hargaBarang, setHargaBarang] = useState(0);

  const handleSubmit = (e) => {
    e.preventDefault();
    const newBarang = { namaBarang, stokBarang, hargaBarang };
    onAdd(newBarang);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Nama Barang:</label>
          <input
            type="text"
            className="form-control"
            value={namaBarang}
            onChange={(e) => setNamaBarang(e.target.value)}
          />
        </div>
        <div className="form-group">
          <label>Stok Barang:</label>
          <input
            type="number"
            className="form-control"
            value={stokBarang}
            onChange={(e) => setStokBarang(Number(e.target.value))}
          />
        </div>
        <div className="form-group">
          <label>Harga Barang:</label>
          <input
            type="number"
            className="form-control"
            value={hargaBarang}
            onChange={(e) => setHargaBarang(Number(e.target.value))}
          />
        </div>
        <button type="submit" className="btn btn-primary mt-2">
          Tambah
        </button>
        <button type="button" className="btn btn-secondary mt-2" onClick={onCancel}>
          Batal
        </button>
      </form>
    </div>
  );
}

export default AddBarang;
