import React, { useState } from 'react';

const SupplierPage = ({ orders, updateStock }) => {
  const [loginData, setLoginData] = useState({ username: '', password: '' });
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setLoginData({ ...loginData, [name]: value });
  };

  const handleLoginSubmit = (e) => {
    e.preventDefault();
    const { username, password } = loginData;
    if (username === 'supplier' && password === 'supplier123') {
      setIsLoggedIn(true);
    } else {
      alert('Invalid credentials');
    }
  };

  const handleRestock = (order) => {
    updateStock(order.barangId, order.quantity);
  };

  return (
    <div>
      {!isLoggedIn ? (
        <form onSubmit={handleLoginSubmit}>
          <h2>Supplier Login</h2>
          <div className="mb-3">
            <label htmlFor="username" className="form-label">Username</label>
            <input
              type="text"
              className="form-control"
              id="username"
              name="username"
              value={loginData.username}
              onChange={handleLoginChange}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="password" className="form-label">Password</label>
            <input
              type="password"
              className="form-control"
              id="password"
              name="password"
              value={loginData.password}
              onChange={handleLoginChange}
              required
            />
          </div>
          <button type="submit" className="btn btn-primary">Login</button>
        </form>
      ) : (
        <div>
          <h2>Supplier Page</h2>
          <h3>Stock Requests</h3>
          <ul className="list-group">
            {orders.map((order) => (
              <li key={order.id} className="list-group-item">
                Barang ID: {order.barangId} - Quantity: {order.quantity}
                <button
                  className="btn btn-success float-end"
                  onClick={() => handleRestock(order)}
                >
                  Restock
                </button>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default SupplierPage;
