import React, { useState } from 'react';
import User from './components/userfd/user';
import TransactionForm from './components/userfd/transactionForm';
import KasirPage from './components/key/KasirPage';
import SupplierPage from './components/key/SupplierPage';
import AdminPage from './components/key/AdminPage';
import BarangList from './components/barang/BarangList';
import AddBarang from './components/barang/AddBarang';
import EditBarang from './components/barang/EditBarang';
import DeleteBarang from './components/barang/DeleteBarang';

import data from './data/barang.json'; 

function App() {
  const [barangs, setBarangs] = useState(data.barangs || []);
  const [transactions, setTransactions] = useState([]);
  const [orders, setOrders] = useState([]);
  const [currentBarang, setCurrentBarang] = useState(null);
  const [showEditBarang, setShowEditBarang] = useState(false);
  const [showAddBarang, setShowAddBarang] = useState(false);
  const [showDeleteBarang, setShowDeleteBarang] = useState(false);
  const [barangToDelete, setBarangToDelete] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  const handleDelete = (id) => {
    setBarangToDelete(id);
    setShowDeleteBarang(true);
  };

  const DeleteBarang = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
    setShowDeleteBarang(false);
  };

  const handleCancelDelete = () => {
    setShowDeleteBarang(false);
  };

  const handleEdit = (id) => {
    const barang = barangs.find((barang) => barang.id === id);
    setCurrentBarang(barang);
    setShowEditBarang(true);
  };

  const editBarang = (updatedBarang) => {
    const updatedBarangs = barangs.map((barang) =>
      barang.id === updatedBarang.id ? updatedBarang : barang
    );
    setBarangs(updatedBarangs);
    setCurrentBarang(null);
    setShowEditBarang(false);
  };

  const handleCancelEdit = () => {
    setCurrentBarang(null);
    setShowEditBarang(false);
  };

  const handleAddBarang = () => {
    setShowAddBarang(true);
  };

  const addBarang = (barang) => {
    const newId = barangs.length > 0 ? Math.max(...barangs.map(b => b.id)) + 1 : 1;
    const newBarang = {
      id: newId,
      ...barang,
    };
    const updatedBarangs = [...barangs, newBarang];
    setBarangs(updatedBarangs);
    setShowAddBarang(false);
  };

  const handleCancelAdd = () => {
    setShowAddBarang(false);
  };

  const handleTransactionSubmit = (transaction) => {
    const newId = transactions.length > 0 ? Math.max(...transactions.map(t => t.id)) + 1 : 1;
    const newTransaction = {
      id: newId,
      status: 'ongoing', // Initial status
      ...transaction,
    };
    setTransactions([...transactions, newTransaction]);
  };

  const handleValidateTransaction = (id, payment, discount) => {
    const updatedTransactions = transactions.map((transaction) =>
      transaction.id === id
        ? { ...transaction, status: 'confirmed', payment: payment, discount: discount }
        : transaction
    );
    setTransactions(updatedTransactions);

    const validatedTransaction = updatedTransactions.find(transaction => transaction.id === id);
    const barang = barangs.find(b => b.id === validatedTransaction.barangId);

    if (barang) {
      const updatedBarangs = barangs.map(b =>
        b.id === barang.id ? { ...b, stock: b.stock - validatedTransaction.quantity } : b
      );
      setBarangs(updatedBarangs);
    }
  };

  const handleRequestSupplier = (id, requestedQuantity) => {
    const newId = orders.length > 0 ? Math.max(...orders.map(o => o.id)) + 1 : 1;
    const newOrder = {
      id: newId,
      barangId: id,
      quantity: requestedQuantity,
      status: 'pending',
    };
    setOrders([...orders, newOrder]);
  };

  const updateStock = (id, stockToAdd) => {
    const updatedBarangs = barangs.map((barang) =>
      barang.id === id ? { ...barang, stock: barang.stock + stockToAdd } : barang
    );
    setBarangs(updatedBarangs);
  };

  const login = () => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      if (username === "bangdig" && password === "bangdig123") {
        setUser(username);
        setIsLogin(true);
        setRole('gudang');
        setCurrentPage('gudang');
      } else if (username === "kasir" && password === "kasir123") {
        setUser(username);
        setIsLogin(true);
        setRole('kasir');
        setCurrentPage('kasir');
      } else if (username === "admin" && password === "admin123") {
        setUser(username);
        setIsLogin(true);
        setRole('admin');
        setCurrentPage('admin');
      } else if (username === "supplier" && password === "supplier123") {
        setUser(username);
        setIsLogin(true);
        setRole('supplier');
        setCurrentPage('supplier');
      } else if (username === "user" && password === "user123") {
        setUser(username);
        setIsLogin(true);
        setRole('user');
        setCurrentPage('user');
      } else {
        alert("Incorrect username or password");
      }
    }, 1000);
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  return (
    <div className="App">
      {isLogin ? (
        <div>
          {role === 'gudang' && currentPage === 'gudang' && <SupplierPage />}
          {role === 'kasir' && currentPage === 'kasir' && <KasirPage />}
          {role === 'admin' && currentPage === 'admin' && <AdminPage />}
          {role === 'supplier' && currentPage === 'supplier' && <SupplierPage />}
          {role === 'user' && currentPage === 'user' && <User />}
        </div>
      ) : (
        <div>
          <h1>Login</h1>
          <input
            type="text"
                       placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={login}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
