import React, { useState, useEffect } from 'react';
import data from '../../data/barang.json'; // Pastikan path ke barang.json benar
import 'bootstrap/dist/css/bootstrap.min.css';


const formatHarga = (harga) => {
  return `Rp. ${Number(harga).toLocaleString('id-ID')}`;
};

const User = ({ barangs, handleTransactionSubmit, onLogout }) => {
  const [selectedBarang, setSelectedBarang] = useState(null);
  const [quantity, setQuantity] = useState(1);

  const handleBarangSelect = (barang) => {
    setSelectedBarang(barang);
  };

  const handleQuantityChange = (e) => {
    setQuantity(parseInt(e.target.value));
  };

  const handleSubmitOrder = () => {
    if (selectedBarang && quantity > 0) {
      const transaction = {
        id: transaction.length > 0 ? Math.max(...transactions.map(t => t.id)) + 1 : 1,
        barangId: selectedBarang.id,
        quantity: quantity,
        itemName: selectedBarang.namaBarang,
        price: selectedBarang.hargaBarang,
        status: 'pending'
      };
      handleTransactionSubmit(transaction);
      setSelectedBarang(null);
      setQuantity(1);
    } else {
      alert("Please select a barang and enter a valid quantity.");
    }
  };

  return (
    <div>
      <h1>User Page</h1>
      <button className="btn btn-secondary mb-3" onClick={onLogout}>
        Logout
      </button>
      <div>
        <h2>Available Barangs</h2>
        <table className="table table-striped">
          <thead className="thead-dark">
            <tr>
              <th>ID</th>
              <th>Nama Barang</th>
              <th>Jenis Barang</th>
              <th>Harga Barang</th>
              <th>Stok Barang</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {barangs.map((barang) => (
              <tr key={barang.id}>
                <td>{barang.id}</td>
                <td>{barang.namaBarang}</td>
                <td>{barang.jenisBarang}</td>
                <td>{formatHarga(barang.hargaBarang)}</td>
                <td>{barang.stokBarang}</td>
                <td>
                  <button
                    className="btn btn-primary"
                    onClick={() => handleBarangSelect(barang)}
                  >
                    Buy
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <div>
        {selectedBarang && (
          <div>
            <h3>Selected Barang: {selectedBarang.namaBarang}</h3>
            <input
              type="number"
              min="1"
              value={quantity}
              onChange={handleQuantityChange}
            />
            <button onClick={handleSubmitOrder}>Order</button>
          </div>
        )}
      </div>
    </div>
  );
};

export default User;
