import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import BarangList from './components/barang/BarangList'; 
import AddBarang from './components/barang/AddBarang';
import KasirPage from './components/key/KasirPage'; 
import User from './components/userfd/user';
import SupplierPage from './components/key/SupplierPage';

function App() {
  const [barangs, setBarangs] = useState([]); 
  const [transactions, setTransactions] = useState([]);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user'); 

  useEffect(() => {
    const storedBarangs = localStorage.getItem('barangs');
    if (storedBarangs) {
      setBarangs(JSON.parse(storedBarangs));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('barangs', JSON.stringify(barangs));
  }, [barangs]);

  const login = () => {
    if (username === "gudang" && password === "gudang123") {
      setUser(username);
      setIsLogin(true);
      setRole('gudang');
      setCurrentPage('gudang'); 
    } else if (username === "kasir" && password === "kasir123") {
      setUser(username);
      setIsLogin(true);
      setRole('kasir');
      setCurrentPage('kasir'); 
    } else if (username === "supplier" && password === "supplier123") {
      setUser(username);
      setIsLogin(true);
      setRole('supplier');
      setCurrentPage('supplier');
    } else if (username === "user" && password === "user123") {
      setUser(username);
      setIsLogin(true);
      setRole('user');
      setCurrentPage('user');
    } else {
      alert("Username atau password salah");
    }
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  const handleDelete = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
  };

  const handleEdit = (id) => {
    // Implement edit functionality here
    console.log(`Edit barang dengan id: ${id}`); 
  };

  const handleAdd = () => {
    setCurrentPage('addBarang');
  };

  const addBarang = (barang) => {
    const newId = barangs.length > 0 
                  ? Math.max(...barangs.map(b => b.id)) + 1 
                  : 1; 
    const newBarang = { ...barang, id: newId };
    setBarangs([...barangs, newBarang]);
    setCurrentPage('gudang');
  };

  const handleCancelAdd = () => {
    setCurrentPage('gudang'); 
  };

  const validateTransaction = (transactionId, payment, discount) => {
    // ... (Your existing transaction validation logic) ...
  };

  const requestSupplier = (barangId, quantityNeeded) => {
    // ... (Your existing supplier request logic) ...
  };

  const updateTransactionStatus = (transactionId, status) => {
    // ... (Your existing logic for updating transaction status) ...
  };

  const handleTransactionSubmit = (transaction) => {
    // ... (Your existing logic for submitting a new transaction) ...
  };

  const updateBarangStock = (barangId, quantity) => {
    // ... (Your existing logic for updating product stock) ...
  };

  const deleteTransaction = (transactionId) => {
    // ... (Your existing logic for deleting a transaction) ...
  };

  const updateStock = (barangId, additionalStock) => {
    // ... (Your existing logic for a supplier to update stock) ...
  };

  return (
    <div className="container mt-4">
      {!isLogin ? ( 
        <div>
          <h1>Login Page</h1>
          <form onSubmit={(e) => { e.preventDefault(); login(); }}>
            <div className="form-group">
              <label>Username:</label>
              <input 
                type="text" 
                className="form-control" 
                value={username} 
                onChange={(e) => setUsername(e.target.value)} 
              />
            </div>
            <div className="form-group">
              <label>Password:</label>
              <input 
                type="password" 
                className="form-control" 
                value={password} 
                onChange={(e) => setPassword(e.target.value)} 
              />
            </div>
            <button type="submit" className="btn btn-primary mt-2">
              Login
            </button>
          </form>
        </div>
      ) : ( 
        <div>
          <header className="mb-4">
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <ul className="navbar-nav mr-auto">
                {/* Conditionally render navigation based on role */}
                {(role === 'gudang' || role === 'kasir') && (
                  <li className="nav-item">
                    <button 
                      className="btn btn-link nav-link" 
                      onClick={() => setCurrentPage('gudang')}
                    >
                      Gudang
                    </button>
                  </li>
                )}
                {role === 'kasir' && (
                  <li className="nav-item">
                    <button 
                      className="btn btn-link nav-link" 
                      onClick={() => setCurrentPage('kasir')}
                    >
                      Kasir
                    </button>
                  </li>
                )}
                {role === 'supplier' && (
                  <li className="nav-item">
                    <button 
                      className="btn btn-link nav-link" 
                      onClick={() => setCurrentPage('supplier')}
                    >
                      Supplier
                    </button>
                  </li>
                )}
                <li className="nav-item">
                  <button 
                    className="btn btn-link nav-link" 
                    onClick={() => setCurrentPage('user')}
                  >
                    User
                  </button>
                </li>
                <li className="nav-item">
                  <button 
                    className="btn btn-link nav-link" 
                    onClick={logout}
                  >
                    Logout
                  </button>
                </li>
              </ul>
            </nav>
          </header>

          {/* Conditionally render the content based on currentPage */}
          {currentPage === 'gudang' && (
            <div>
              <h1>Gudang Page</h1>
              <BarangList 
                barangs={barangs} 
                onDelete={handleDelete} 
                onEdit={handleEdit} 
              />
              <button className="btn btn-primary mt-3" onClick={handleAdd}>
                Tambah Barang
              </button>
            </div>
          )}

          {currentPage === 'addBarang' && (
            <div>
              <h1>Tambah Barang</h1>
              <AddBarang onAdd={addBarang} onCancel={handleCancelAdd} />
            </div>
          )}

          {currentPage === 'kasir' && (
            <div>
              <h1>Kasir Page</h1>
              <KasirPage 
                transactions={transactions} 
                barangs={barangs} 
                onValidateTransaction={validateTransaction} 
                onRequestSupplier={requestSupplier} 
                onUpdateTransactionStatus={updateTransactionStatus}
                onDeleteTransaction={deleteTransaction}
              />
            </div>
          )}

          {currentPage === 'supplier' && (
            <div>
              <h1>Supplier Page</h1>
              <SupplierPage 
                barangs={barangs} 
                onUpdateStock={updateStock} 
              />
            </div>
          )}

          {currentPage === 'user' && (
            <div>
              <h1>User Page</h1>
              <User 
                barangs={barangs} 
                transactions={transactions} 
                handleTransactionSubmit={handleTransactionSubmit} 
                onLogout={logout} 
              /> 
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
