import React, { useState } from 'react';
import BarangList from './components/barang/BarangList';
import data from './data/barang.json';

function App() {
  const [barangs, setBarangs] = useState(data.barangs || []);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  const login = () => {
    // login logic
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  const handleAddBarang = (newBarang) => {
    setBarangs([...barangs, newBarang]);
  };

  const handleEditBarang = (updatedBarang) => {
    const updatedBarangs = barangs.map((barang) =>
      barang.id === updatedBarang.id ? updatedBarang : barang
    );
    setBarangs(updatedBarangs);
  };

  const handleDeleteBarang = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
  };

  return (
    <div className="App">
      {isLogin ? (
        <div>
          {role === 'gudang' && currentPage === 'gudang' && (
            <BarangList
              barangs={barangs}
              onAdd={handleAddBarang}
              onEdit={handleEditBarang}
              onDelete={handleDeleteBarang}
            />
          )}
          {/* Other role-based pages */}
        </div>
      ) : (
        <div>
          <h1>Login</h1>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={login}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
