import React, { useState } from 'react';
import User from './pages/User';
import KasirPage from './components/kasir/KasirPage';
import data from './data/barang.json';

function App() {
  const [barangs, setBarangs] = useState(data.barangs || []);
  const [transactions, setTransactions] = useState([]);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  const login = () => {
    if (username === "gudang" && password === "gudang123") {
      setUser(username);
      setIsLogin(true);
      setRole('gudang');
      setCurrentPage('gudang');
    } else if (username === "kasir" && password === "kasir123") {
      setUser(username);
      setIsLogin(true);
      setRole('kasir');
      setCurrentPage('kasir');
    } else {
      alert("Incorrect username or password");
    }
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  const validateTransaction = (transactionId, payment, discount) => {
    setTransactions(transactions.map(t => t.id === transactionId ? { ...t, status: 'validated', payment, discount } : t));
  };

  const requestSupplier = (barangId, quantityNeeded) => {
    console.log(`Requesting ${quantityNeeded} more of barang id: ${barangId}`);
  };

  const addTransaction = (transaction) => {
    setTransactions([...transactions, transaction]);
  };

  return (
    <div className="App">
      {isLogin ? (
        <div>
          <button className="btn btn-secondary mb-3" onClick={logout}>
            Logout
          </button>
          {role === 'kasir' ? (
            <KasirPage
              transactions={transactions}
              barangs={barangs}
              onValidateTransaction={validateTransaction}
              onRequestSupplier={requestSupplier}
              onAddTransaction={addTransaction}
            />
          ) : (
            <User
              handleTransactionSubmit={addTransaction}
              onLogout={logout}
            />
          )}
        </div>
      ) : (
        <div>
          <h1>Login</h1>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={login}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
