import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import BarangList from './components/barang/BarangList';
import AddBarang from './components/barang/AddBarang';
import KasirPage from './components/key/KasirPage';
import User from './components/userfd/user';
import barangData from './data/barang.json';

function App() {
  const [barangs, setBarangs] = useState(barangData.barangs || []);
  const [transactions, setTransactions] = useState([]);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  useEffect(() => {
    // Muat data transaksi dari transactions.json
    fetch('../transactions.json')
      .then(response => response.json())
      .then(data => setTransactions(data.transactions))
      .catch(error => console.error('Error fetching transactions:', error));
  }, []);

  const login = () => {
    if (username === "gudang" && password === "gudang123") {
      setUser(username);
      setIsLogin(true);
      setRole('gudang');
      setCurrentPage('gudang');
    } else if (username === "kasir" && password === "kasir123") {
      setUser(username);
      setIsLogin(true);
      setRole('kasir');
      setCurrentPage('kasir');
    } else if (username === "user" && password === "user123") {
      setUser(username);
      setIsLogin(true);
      setRole('user');
      setCurrentPage('user');
    } else {
      alert("Incorrect username or password");
    }
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  const handleDelete = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
  };

  const handleEdit = (id) => {
    console.log(`Edit barang with id: ${id}`);
  };

  const handleAdd = () => {
    setCurrentPage('addBarang');
  };

  const addBarang = (barang) => {
    const newId = barangs.length > 0 ? Math.max(...barangs.map(b => b.id)) + 1 : 1;
    const newBarang = { ...barang, id: newId };
    setBarangs([...barangs, newBarang]);
    setCurrentPage('gudang');
  };

  const handleCancelAdd = () => {
    setCurrentPage('gudang');
  };

  const validateTransaction = (transactionId, payment, discount) => {
    const transaction = transactions.find(t => t.id === transactionId);
    if (transaction) {
      updateBarangStock(transaction.barangId, transaction.quantity);
    } else {
      console.log(`Transaction with ID ${transactionId} not found.`);
    }

    setTransactions(transactions.map((transaction) =>
      transaction.id === transactionId ? { ...transaction, status: 'process', payment, discount } : transaction
    ));
  };

  const requestSupplier = (barangId, quantityNeeded) => {
    console.log(`Requesting ${quantityNeeded} more units of barang ID ${barangId}`);
    // Implement logic to request stock from supplier
  };

  const updateTransactionStatus = (transactionId, status) => {
    setTransactions(transactions.map((transaction) =>
      transaction.id === transactionId ? { ...transaction, status: status } : transaction
    ));
  };

  const handleTransactionSubmit = (transaction) => {
    const newTransaction = {
      ...transaction,
      id: transactions.length > 0 ? Math.max(...transactions.map(t => t.id)) + 1 : 1,
      status: 'pending'
    };
    setTransactions([...transactions, newTransaction]);
  };

  const handleTransactionDelete = (transactionId) => {
    setTransactions(transactions.filter(transaction => transaction.id !== transactionId));
  };

  const updateBarangStock = (barangId, quantity) => {
    setBarangs(barangs.map((barang) =>
      barang.id === barangId ? { ...barang, stokBarang: barang.stokBarang - quantity } : barang
    ));
  };

  return (
    <div className="container mt-4">
      {!isLogin ? (
        <div>
          <h1>Login Page</h1>
          <form onSubmit={(e) => { e.preventDefault(); login(); }}>
            <div className="form-group">
              <label>Username:</label>
              <input type="text" className="form-control" value={username} onChange={(e) => setUsername(e.target.value)} />
            </div>
            <div className="form-group">
              <label>Password:</label>
              <input type="password" className="form-control" value={password} onChange={(e) => setPassword(e.target.value)} />
            </div>
            <button type="submit" className="btn btn-primary mt-2">Login</button>
          </form>
        </div>
      ) : (
        <div>
          <header className="mb-4">
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <ul className="navbar-nav mr-auto">
                <li className="nav-item">
                  <button className="btn btn-link nav-link" onClick={() => setCurrentPage('gudang')}>Gudang</button>
                </li>
                <li className="nav-item">
                  <button className="btn btn-link nav-link" onClick={() => setCurrentPage('kasir')}>Kasir</button>
                </li>
                <li className="nav-item">
                  <button className="btn btn-link nav-link" onClick={() => setCurrentPage('user')}>User</button>
                </li>
                <li className="nav-item">
                  <button className="btn btn-link nav-link" onClick={logout}>Logout</button>
                </li>
              </ul>
            </nav>
          </header>

          {currentPage === 'gudang' && (
            <div>
              <h1>Gudang Page</h1>
              <BarangList barangs={barangs} onDelete={handleDelete} onEdit={handleEdit} />
              <button className="btn btn-primary mt-3" onClick={handleAdd}>Add Barang</button>
            </div>
          )}

          {currentPage === 'addBarang' && (
            <div>
              <h1>Add Barang</h1>
              <AddBarang onAdd={addBarang} onCancel={handleCancelAdd} />
            </div>
          )}

          {currentPage === 'kasir' && (
            <div>
              <h1>Kasir Page</h1>
              <KasirPage
                transactions={transactions}
                barangs={barangs}
                onValidateTransaction={validateTransaction}
                onRequestSupplier={requestSupplier}
                onUpdateTransactionStatus={updateTransactionStatus}
                onDeleteTransaction={handleTransactionDelete}
              />
            </div>
          )}

          {currentPage === 'user' && (
            <div>
              <h1>User Page</h1>
              <User
                barangs={barangs}
                transactions={transactions}
                handleTransactionSubmit={handleTransactionSubmit}
                onLogout={logout}
              />
            </div>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
