import React from 'react';
import PropTypes from 'prop-types';
import 'bootstrap/dist/css/bootstrap.min.css';

const formatHarga = (harga) => {
  return `Rp. ${Number(harga).toLocaleString('id-ID')}`;
};

const BarangList = ({ barangs, onDelete, onEdit, onAdd, onSelect }) => {
  return (
    <div className="container mt-4">
      <h2 className="mb-4">List of Barang</h2>
      <div className="table-responsive"> {/* Tambahkan table-responsive untuk responsivitas */}
        <table className="table table-striped">
          <thead className="thead-dark">
            <tr>
              <th>ID</th>
              <th>Nama Barang</th>
              <th>Jenis Barang</th>
              <th>Harga Barang</th>
              <th>Stok Barang</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {barangs.map((barang) => (
              <tr key={barang.id} onClick={() => onSelect(barang)}>
                <td>{barang.id}</td>
                <td>{barang.namaBarang}</td>
                <td>{barang.jenisBarang}</td>
                <td>{formatHarga(barang.hargaBarang)}</td>
                <td>{barang.stokBarang}</td>
                <td className="d-flex justify-content-center"> {/* Atur tombol secara horizontal */}
                  <button
                    className="btn btn-primary mr-2"
                    onClick={(e) => {
                      e.stopPropagation();
                      onEdit(barang.id);
                    }}
                  >
                    Edit
                  </button>
                  <button
                    className="btn btn-danger"
                    onClick={(e) => {
                      e.stopPropagation();
                      onDelete(barang.id);
                    }}
                  >
                    Delete
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
      <button className="btn btn-success mt-3" onClick={onAdd}>
        Add New Barang
      </button>
    </div>
  );
};

BarangList.propTypes = {
  barangs: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      namaBarang: PropTypes.string.isRequired,
      jenisBarang: PropTypes.string.isRequired,
      hargaBarang: PropTypes.string.isRequired,
      stokBarang: PropTypes.number.isRequired,
    })
  ).isRequired,
  onDelete: PropTypes.func.isRequired,
  onEdit: PropTypes.func.isRequired,
  onAdd: PropTypes.func.isRequired,
  onSelect: PropTypes.func.isRequired,
};

export default BarangList;
