import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Table, Alert } from 'react-bootstrap';

function SupplierPage({ barangs, onUpdateStock }) {
  const [selectedBarangId, setSelectedBarangId] = useState(null);
  const [additionalStock, setAdditionalStock] = useState(0);
  const [showAlert, setShowAlert] = useState(false);

  const handleUpdateStock = () => {
    if (selectedBarangId && additionalStock > 0) {
      onUpdateStock(selectedBarangId, additionalStock);
      setSelectedBarangId(null);
      setAdditionalStock(0);
      setShowAlert(false);
    } else {
      setShowAlert(true);
    }
  };

  return (
    <Container className="mt-4">
      {showAlert && (
        <Alert variant="danger" onClose={() => setShowAlert(false)} dismissible>
          Please select a barang and enter a valid stock quantity.
        </Alert>
      )}

      <Row className="mb-4">
        <Col>
          <h2>List of Barang</h2>
          <Table striped bordered hover>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nama Barang</th>
                <th>Stok Barang</th>
              </tr>
            </thead>
            <tbody>
              {barangs.map((barang) => (
                <tr key={barang.id}>
                  <td>{barang.id}</td>
                  <td>{barang.namaBarang}</td>
                  <td>{barang.stokBarang}</td>
                </tr>
              ))}
            </tbody>
          </Table>
        </Col>
      </Row>

      <Row>
        <Col>
          <h2>Update Stock</h2>
          <Form>
            <Form.Group controlId="barangSelect">
              <Form.Label>Select Barang:</Form.Label>
              <Form.Control
                as="select"
                value={selectedBarangId || ''}
                onChange={(e) => setSelectedBarangId(parseInt(e.target.value))}
              >
                <option value="" disabled>Select Barang</option>
                {barangs.map((barang) => (
                  <option key={barang.id} value={barang.id}>
                    {barang.namaBarang}
                  </option>
                ))}
              </Form.Control>
            </Form.Group>

            <Form.Group controlId="additionalStock">
              <Form.Label>Additional Stock:</Form.Label>
              <Form.Control
                type="number"
                value={additionalStock}
                onChange={(e) => setAdditionalStock(parseInt(e.target.value))}
              />
            </Form.Group>

            <Button variant="primary" onClick={handleUpdateStock}>
              Update Stock
            </Button>
          </Form>
        </Col>
      </Row>
    </Container>
  );
}

export default SupplierPage;
