import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import Customer from './components/Customer/Customer';
import Cashier from './components/Cashier/Cashier';
import Header from './components/common/Header';
import Footer from './components/common/Footer';
import LoginForm from './components/common/LoginForm';
import { TransactionProvider } from './context/TransactionContext';

const App = () => {
  const [user, setUser] = useState(null);
  const [role, setRole] = useState('user'); // Menambahkan state untuk peran pengguna

  const handleLogin = (credentials) => {
    // Lakukan verifikasi login disini, misalnya dengan backend atau data statis
    // Untuk contoh, kita hanya melakukan verifikasi username saja
    const { username } = credentials;

    // Simulasikan verifikasi username
    if (username === 'admin') {
      setUser({ username, role: 'admin' });
      setRole('admin');
    } else if (username === 'cashier') {
      setUser({ username, role: 'cashier' });
      setRole('cashier');
    } else if (username === 'customer') {
      setUser({ username, role: 'customer' });
      setRole('customer');
    } else {
      setUser(null);
      setRole('user');
    }
  };

  const handleLogout = () => {
    setUser(null);
    setRole('user');
  };

  return (
    <TransactionProvider>
      <Router>
        <div>
          <Header user={user} onLogout={handleLogout} />
          <main>
            <Routes>
              <Route path="/customer" element={<Customer />} />
              <Route path="/cashier" element={<Cashier />} />
              <Route path="/login" element={<LoginForm onSubmit={handleLogin} />} />
              {user ? (
                <>
                  {role === 'admin' && <Route path="/" element={<Navigate to="/cashier" />} />}
                  {role === 'cashier' && <Route path="/" element={<Navigate to="/cashier" />} />}
                  {role === 'customer' && <Route path="/" element={<Navigate to="/customer" />} />}
                </>
              ) : (
                <Route path="/" element={<Navigate to="/login" />} />
              )}
            </Routes>
          </main>
          <Footer />
        </div>
      </Router>
    </TransactionProvider>
  );
};

export default App;
