import React, { useState } from 'react';

const AdminPage = ({ addUser }) => {
  const [newUserUsername, setNewUserUsername] = useState('');
  const [newUserPassword, setNewUserPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('user'); // Default role
  const [currentPage, setCurrentPage] = useState('gudang'); // Default page

  const handleSubmit = (e) => {
    e.preventDefault();
    addUser(newUserUsername, newUserPassword, newUserRole);
    setNewUserUsername('');
    setNewUserPassword('');
    setNewUserRole('user'); // Reset role ke default
  };

  const navigateTo = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Admin Page</h2>

      <div>
        <button onClick={() => navigateTo('gudang')}>Gudang</button>
        <button onClick={() => navigateTo('supplier')}>Supplier</button>
        <button onClick={() => navigateTo('kasir')}>Kasir</button>
      </div>

      {currentPage === 'gudang' && (
        <div className="card">
          <div className="card-header">Add New User</div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              {/* Form untuk menambahkan pengguna */}
            </form>
          </div>
        </div>
      )}

      {currentPage === 'supplier' && (
        // Tambahkan tampilan untuk halaman supplier di sini
        <div>
          <h3>Supplier Page</h3>
          {/* Isi dengan konten halaman supplier */}
        </div>
      )}

      {currentPage === 'kasir' && (
        // Tambahkan tampilan untuk halaman kasir di sini
        <div>
          <h3>Kasir Page</h3>
          {/* Isi dengan konten halaman kasir */}
        </div>
      )}
    </div>
  );
};

export default AdminPage;
