import React, { useState } from 'react';
import BarangList from './components/barang/BarangList';
import data from './data/barang.json';

function App() {
  const [barangs, setBarangs] = useState(data.barangs || []);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  const handleAddBarang = () => {
    const newId = barangs.length > 0 ? Math.max(...barangs.map(b => b.id)) + 1 : 1;
    const newBarang = {
      id: newId,
      namaBarang: "New Barang",
      jenisBarang: "Kategori",
      hargaBarang: "10000",
      stokBarang: "10"
    };
    setBarangs([...barangs, newBarang]);
  };

  const handleEditBarang = (id) => {
    const updatedBarangs = barangs.map((barang) =>
      barang.id === id ? { ...barang, namaBarang: `${barang.namaBarang} Edited` } : barang
    );
    setBarangs(updatedBarangs);
  };

  const handleDeleteBarang = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
  };

  const login = () => {
    if (username === "bangdig" && password === "bangdig123") {
      setUser(username);
      setIsLogin(true);
      setRole('gudang');
      setCurrentPage('gudang');
    } else if (username === "gudang" && password === "gudang123") {
      setUser(username);
      setIsLogin(true);
      setRole('kasir');
      setCurrentPage('kasir');
    } else if (username === "kasir" && password === "kasir123") {
      setUser(username);
      setIsLogin(true);
      setRole('admin');
      setCurrentPage('admin');
    } else if (username === "supplier" && password === "supplier123") {
      setUser(username);
      setIsLogin(true);
      setRole('supplier');
      setCurrentPage('supplier');
    } else if (username === "user" && password === "user123") {
      setUser(username);
      setIsLogin(true);
      setRole('user');
      setCurrentPage('user');
    } else {
      alert("Incorrect username or password");
    }
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  return (
    <div className="App">
      {isLogin ? (
        <div>
          {role === 'gudang' && currentPage === 'gudang' && (
            <BarangList
              barangs={barangs}
              onAdd={handleAddBarang}
              onEdit={handleEditBarang}
              onDelete={handleDeleteBarang}
            />
          )}
          <button onClick={logout}>Logout</button>
        </div>
      ) : (
        <div>
          <h1>Login</h1>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={login}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
