import React, { useState } from 'react';
import BarangList from '../components/barang/BarangList'; // Pastikan komponen BarangList sudah diimpor

function User({ handleTransactionSubmit }) {
  const [selectedBarang, setSelectedBarang] = useState(null);
  const [quantity, setQuantity] = useState(1);

  const handleBarangSelect = (barang) => {
    setSelectedBarang(barang);
  };

  const handleQuantityChange = (e) => {
    setQuantity(parseInt(e.target.value));
  };

  const handleSubmitOrder = () => {
    if (selectedBarang && quantity > 0) {
      const transaction = {
        barangId: selectedBarang.id,
        quantity: quantity,
      };
      handleTransactionSubmit(transaction);
      setSelectedBarang(null);
      setQuantity(1);
    } else {
      alert("Please select a barang and enter a valid quantity.");
    }
  };

  return (
    <div>
      <h1>User Page</h1>
      <div>
        <h2>Available Barangs</h2>
        <BarangList onSelectBarang={handleBarangSelect} />
      </div>
      <div>
        {selectedBarang && (
          <div>
            <h3>Selected Barang: {selectedBarang.name}</h3>
            <input
              type="number"
              min="1"
              value={quantity}
              onChange={handleQuantityChange}
            />
            <button onClick={handleSubmitOrder}>Order</button>
          </div>
        )}
      </div>
    </div>
  );
}

export default User;
