import React, { useState } from 'react';

const AdminPage = ({ addUser }) => {
  const [newUserUsername, setNewUserUsername] = useState('');
  const [newUserPassword, setNewUserPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('user'); // Default role
  const [currentPage, setCurrentPage] = useState('gudang'); // Default page

  const handleSubmit = (e) => {
    e.preventDefault();
    addUser(newUserUsername, newUserPassword, newUserRole);
    setNewUserUsername('');
    setNewUserPassword('');
    setNewUserRole('user'); // Reset role ke default
  };

  const navigateTo = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Admin Page</h2>
      {currentPage === 'gudang' && (
        <div className="card mt-4">
          <div className="card-header">Add New User</div>
          <div className="card-body">
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label htmlFor="newUserUsername">Username:</label>
                <input 
                  type="text" 
                  className="form-control" 
                  id="newUserUsername" 
                  value={newUserUsername} 
                  onChange={(e) => setNewUserUsername(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="newUserPassword">Password:</label>
                <input 
                  type="password" 
                  className="form-control" 
                  id="newUserPassword" 
                  value={newUserPassword} 
                  onChange={(e) => setNewUserPassword(e.target.value)}
                  required
                />
              </div>
              <div className="form-group">
                <label htmlFor="newUserRole">Role:</label>
                <select 
                  className="form-control" 
                  id="newUserRole" 
                  value={newUserRole} 
                  onChange={(e) => setNewUserRole(e.target.value)}
                >
                  <option value="user">User</option>
                  <option value="gudang">Gudang</option>
                  <option value="kasir">Kasir</option>
                  <option value="supplier">Supplier</option>
                </select>
              </div>
              <button type="submit" className="btn btn-primary">Add User</button>
            </form>
          </div>
        </div>
      )}

      {currentPage === 'supplier' && (
        // Tambahkan tampilan untuk halaman supplier di sini
        <div>
          <h3>Supplier Page</h3>
          {/* Isi dengan konten halaman supplier */}
        </div>
      )}

      {currentPage === 'kasir' && (
        // Tambahkan tampilan untuk halaman kasir di sini
        <div>
          <h3>Kasir Page</h3>
          {/* Isi dengan konten halaman kasir */}
        </div>
      )}
    </div>
  );
};

export default AdminPage;
