import React, { useState, useEffect } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import BarangList from './components/barang/BarangList';
import AddBarang from './components/barang/AddBarang';
import KasirPage from './components/key/KasirPage';
import User from './components/userfd/user';
import SupplierPage from './components/key/SupplierPage';
import AdminPage from './components/key/AdminPage';
imp; // Import Navbar component
import barangData from './data/barang.json';
import transactionData from './data/transactions.json';

function App() {
  const [barangs, setBarangs] = useState([]);
  const [transactions, setTransactions] = useState([]);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');
  const [showAddBarangModal, setShowAddBarangModal] = useState(false);

  useEffect(() => {
    setBarangs(barangData.barangs);
    localStorage.setItem('barangs', JSON.stringify(barangData.barangs));

    setTransactions(transactionData.transactions);
    localStorage.setItem('transactions', JSON.stringify(transactionData.transactions));
  }, []);

  useEffect(() => {
    localStorage.setItem('barangs', JSON.stringify(barangs));
  }, [barangs]);

  useEffect(() => {
    localStorage.setItem('transactions', JSON.stringify(transactions));
  }, [transactions]);

  const userAccounts = [
    { username: "gudang", password: "gudang123", role: "gudang" },
    { username: "kasir", password: "kasir123", role: "kasir" },
    { username: "supplier", password: "supplier123", role: "supplier" },
    { username: "admin", password: "admin123", role: "admin" },
    { username: "user", password: "user123", role: "user" }
  ];

  const login = () => {
    const account = userAccounts.find(
      (acc) => acc.username === username && acc.password === password
    );

    if (account) {
      setUser(account.username);
      setIsLogin(true);
      setRole(account.role);
      setCurrentPage(account.role);
    } else {
      alert("Username atau password salah");
    }
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  const handleDelete = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
  };

  const handleEdit = (id) => {
    console.log(`Edit barang dengan id: ${id}`);
  };

  const handleAdd = () => {
    setShowAddBarangModal(true);
  };

  const addBarang = (barang) => {
    const newBarang = {
      id: barangs.length > 0 ? Math.max(...barangs.map(b => b.id)) + 1 : 1,
      ...barang
    };
    setBarangs([...barangs, newBarang]);
    setShowAddBarangModal(false);
  };

  const handleTransactionSubmit = (transaction) => {
    const newTransaction = {
      id: transactions.length > 0 ? Math.max(...transactions.map(t => t.id)) + 1 : 1,
      ...transaction
    };
    setTransactions([...transactions, newTransaction]);
  };

  return (
    <div className="App">
      {isLogin && <Navbar role={role} setCurrentPage={setCurrentPage} onLogout={logout} />}
      {!isLogin ? (
        <div className="container mt-4">
          <h2>Login</h2>
          <form onSubmit={(e) => { e.preventDefault(); login(); }}>
            <div className="form-group">
              <label htmlFor="username">Username:</label>
              <input
                type="text"
                className="form-control"
                id="username"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password:</label>
              <input
                type="password"
                className="form-control"
                id="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
            </div>
            <button type="submit" className="btn btn-primary">Login</button>
          </form>
        </div>
      ) : (
        <div className="container mt-4">
          {currentPage === 'gudang' && (
            <BarangList
              barangs={barangs}
              onDelete={handleDelete}
              onEdit={handleEdit}
              onAdd={handleAdd}
            />
          )}
          {currentPage === 'supplier' && (
            <SupplierPage />
          )}
          {currentPage === 'kasir' && (
            <KasirPage
              transactions={transactions}
              handleTransactionSubmit={handleTransactionSubmit}
            />
          )}
          {currentPage === 'user' && (
            <User
              transactions={transactions}
              barangs={barangs}
              handleTransactionSubmit={handleTransactionSubmit}
              onLogout={logout}
            />
          )}
          {currentPage === 'admin' && (
            <AdminPage
              barangs={barangs}
              transactions={transactions}
              addUser={(username, password, role) => {
                userAccounts.push({ username, password, role });
              }}
            />
          )}
          {showAddBarangModal && (
            <AddBarang
              onAdd={addBarang}
              onCancel={() => setShowAddBarangModal(false)}
            />
          )}
        </div>
      )}
    </div>
  );
}

export default App;
