import React, { useState } from 'react';
import User from './components/userfd/user';
import KasirPage from './components/key/KasirPage';
import SupplierPage from './components/key/SupplierPage';
import AdminPage from './components/key/AdminPage';
import BarangList from './components/barang/BarangList';

import data from './data/barang.json';

function App() {
  const [barangs, setBarangs] = useState(data.barangs || []);
  const [transactions, setTransactions] = useState([]);
  const [orders, setOrders] = useState([]);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  const handleRequestSupplier = (id, requestedQuantity) => {
    const newId = orders.length > 0 ? Math.max(...orders.map(o => o.id)) + 1 : 1;
    const newOrder = {
      id: newId,
      barangId: id,
      quantity: requestedQuantity,
      status: 'pending',
    };
    setOrders([...orders, newOrder]);
  };

  const updateStock = (id, stockToAdd) => {
    const updatedBarangs = barangs.map((barang) =>
      barang.id === id ? { ...barang, stock: barang.stock + stockToAdd } : barang
    );
    setBarangs(updatedBarangs);
  };

  const login = () => {
    if (username === "bangdig" && password === "bangdig123") {
      setUser(username);
      setIsLogin(true);
      setRole('gudang');
      setCurrentPage('gudang');
    } else if (username === "gudang" && password === "gudang123") {
      setUser(username);
      setIsLogin(true);
      setRole('kasir');
      setCurrentPage('kasir');
    } else if (username === "kasir" && password === "kasir123") {
      setUser(username);
      setIsLogin(true);
      setRole('admin');
      setCurrentPage('admin');
    } else if (username === "supplier" && password === "supplier123") {
      setUser(username);
      setIsLogin(true);
      setRole('supplier');
      setCurrentPage('supplier');
    } else if (username === "user" && password === "user123") {
      setUser(username);
      setIsLogin(true);
      setRole('user');
      setCurrentPage('user');
    } else {
      alert("Incorrect username or password");
    }
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  return (
    <div className="App">
      {isLogin ? (
        <div>
          {role === 'gudang' && currentPage === 'gudang' && <BarangList barangs={barangs} />}
          {role === 'kasir' && currentPage === 'kasir' && <KasirPage />}
          {role === 'admin' && currentPage === 'admin' && <AdminPage />}
          {role === 'supplier' && currentPage === 'supplier' && (
            <SupplierPage orders={orders} updateStock={updateStock} />
          )}
          {role === 'user' && currentPage === 'user' && <User />}
          <button onClick={logout}>Logout</button>
        </div>
      ) : (
        <div>
          <h1>Login</h1>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={login}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
