import React, { useState } from "react";
import 'bootstrap/dist/css/bootstrap.min.css';
import BarangList from "./components/barang/BarangList";
import EditBarang from "./components/barang/EditBarang";
import AddBarang from "./components/barang/AddBarang";
import DeleteBarang from "./components/barang/DeleteBarang";
import data from "./data/barang.json";
import UserPage from "./components/userfd/user";
import KasirPage from "./components/key/KasirPage";
import AdminPage from "./components/key/AdminPage";

function App() {
  const [barangs, setBarangs] = useState(data.barangs || []);
  const [transactions, setTransactions] = useState([]);
  const [currentBarang, setCurrentBarang] = useState(null);
  const [showEditBarang, setShowEditBarang] = useState(false);
  const [showAddBarang, setShowAddBarang] = useState(false);
  const [showDeleteBarang, setShowDeleteBarang] = useState(false);
  const [barangToDelete, setBarangToDelete] = useState(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isLogin, setIsLogin] = useState(false);
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  const handleDelete = (id) => {
    setBarangToDelete(id);
    setShowDeleteBarang(true);
  };

  const deleteBarang = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
    setShowDeleteBarang(false);
  };

  const handleCancelDelete = () => {
    setShowDeleteBarang(false);
  };

  const handleEdit = (id) => {
    const barang = barangs.find((barang) => barang.id === id);
    setCurrentBarang(barang);
    setShowEditBarang(true);
  };

  const editBarang = (updatedBarang) => {
    const updatedBarangs = barangs.map((barang) =>
      barang.id === updatedBarang.id ? updatedBarang : barang
    );
    setBarangs(updatedBarangs);
    setCurrentBarang(null);
    setShowEditBarang(false);
  };

  const handleCancelEdit = () => {
    setCurrentBarang(null);
    setShowEditBarang(false);
  };

  const handleAddBarang = () => {
    setShowAddBarang(true);
  };

  const addBarang = (barang) => {
    const newId = barangs.length > 0 ? Math.max(...barangs.map(b => b.id)) + 1 : 1;
    const newBarang = {
      id: newId,
      ...barang,
    };
    const updatedBarangs = [...barangs, newBarang];
    setBarangs(updatedBarangs);
    setShowAddBarang(false);
  };

  const handleCancelAdd = () => {
    setShowAddBarang(false);
  };

  const handleTransactionSubmit = (transaction) => {
    const newId = transactions.length > 0 ? Math.max(...transactions.map(t => t.id)) + 1 : 1;
    const newTransaction = {
      id: newId,
      status: 'ongoing', // Initial status
      ...transaction,
    };
    setTransactions([...transactions, newTransaction]);
  };

  const handleValidateTransaction = (id, payment, discount) => {
    const updatedTransactions = transactions.map((transaction) =>
      transaction.id === id
        ? { ...transaction, status: 'confirmed', payment: payment, discount: discount }
        : transaction
    );
    setTransactions(updatedTransactions);
  };

  const updateStock = (id, stockToAdd) => {
    const updatedBarangs = barangs.map((barang) =>
      barang.id === id ? { ...barang, stock: barang.stock + stockToAdd } : barang
    );
    setBarangs(updatedBarangs);
  };

  const login = (username, password) => {
    setIsLoading(true);
    setTimeout(() => {
      setIsLoading(false);
      if (username === "bangdig" && password === "bangdig123") {
        setUser(username);
        setIsLogin(true);
        setRole('gudang');
        setCurrentPage('gudang');
      } else if (username === "kasir" && password === "kasir123") {
        setUser(username);
        setIsLogin(true);
        setRole('kasir');
        setCurrentPage('kasir');
      } else if (username === "admin" && password === "admin123") {
        setUser(username);
        setIsLogin(true);
        setRole('admin');
        setCurrentPage('admin');
      } else {
        alert("Incorrect username or password");
      }
    }, 1000);
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  return (
    <div className="App container mt-5">
      <h1>Data Barang</h1>
      {role !== 'user' && !isLogin ? (
        <div className="login-form">
          <h2>Login</h2>
          <form
            onSubmit={(e) => {
              e.preventDefault();
              const formData = new FormData(e.target);
              const username = formData.get("username");
              const password = formData.get("password");
              login(username, password);
            }}
          >
            <div className="mb-3">
              <label htmlFor="username">Username</label>
              <input type="text" className="form-control" id="username" name="username" required />
            </div>
            <div className="mb-3">
              <label htmlFor="password">Password</label>
              <input type="password" className="form-control" id="password" name="password" required />
            </div>
            <button type="submit" className="btn btn-primary">
              {isLoading ? "Loading..." : "Login"}
            </button>
          </form>
        </div>
      ) : (
        <>
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <a className="navbar-brand" href="#">MyApp</a>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav">
                {role === 'gudang' && (
                  <>
                    <li className="nav-item">
                      <button className="nav-link btn" onClick={() => setCurrentPage('gudang')}>Gudang</button>
                    </li>
                    <li className="nav-item">
                      <button className="nav-link btn" onClick={() => setCurrentPage('kasir')}>Kasir</button>
                    </li>
                    <li className="nav-item">
                      <button className="nav-link btn" onClick={() => setCurrentPage('admin')}>Admin</button>
                    </li>
                  </>
                )}
                {role === 'kasir' && (
                  <li className="nav-item">
                    <button className="nav-link btn" onClick={() => setCurrentPage('kasir')}>Kasir</button>
                  </li>
                )}
                {role === 'user' && (
                  <li className="nav-item">
                    <button className="nav-link btn" onClick={() => setCurrentPage('user')}>User</button>
                  </li>
                )}
              </ul>
            </div>
            <button className="btn btn-secondary" onClick={logout}>Logout</button>
          </nav>
          {currentPage === 'gudang' && role === 'gudang' && (
            <>
              {showDeleteBarang && (
                <DeleteBarang
                  barang={barangs.find((barang) => barang.id === barangToDelete)}
                  deleteBarang={deleteBarang}
                  onCancel={handleCancelDelete}
                />
              )}
              {showEditBarang && (
                <EditBarang
                  barang={currentBarang}
                  editBarang={editBarang}
                  onCancel={handleCancelEdit}
                />
              )}
              {showAddBarang && (
                <AddBarang addBarang={addBarang} onCancel={handleCancelAdd} />
              )}
              <BarangList
                barangs={barangs}
                onDelete={handleDelete}
                onEdit={handleEdit}
                onAdd={handleAddBarang}
                username={user}
              />
            </>
          )}
          {currentPage === 'kasir' && (role === 'gudang' || role === 'kasir') && (
            <>
              <KasirPage
                transactions={transactions}
                onValidateTransaction={handleValidateTransaction}
              />
              {role === 'admin' && (
                <AdminPage
                  barangs={barangs}
                  updateStock={updateStock}
                />
              )}
            </>
          )}
          {currentPage === 'user' && role === 'user' && (
            <UserPage
              user={user}
              transactions={transactions}
              onTransactionSubmit={handleTransactionSubmit}
              onLogout={logout}
            />
          )}
        </>
      )}
    </div>
  );
}

export default App;
