import React, { useState } from 'react';
import PropTypes from 'prop-types';
import BarangList from '../BarangList'; // Pastikan untuk mengimpor BarangList

const KasirPage = ({ transactions, barangs, onValidateTransaction, onRequestSupplier, onAddTransaction }) => {
  const [payment, setPayment] = useState('');
  const [discount, setDiscount] = useState('');
  const [selectedTransaction, setSelectedTransaction] = useState(null);
  const [selectedBarang, setSelectedBarang] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [errorMessage, setErrorMessage] = useState('');

  const handleSelectTransaction = (transaction) => {
    setSelectedTransaction(transaction);
    setPayment('');
    setDiscount('');
    setErrorMessage('');
  };

  const handleValidationSubmit = (e) => {
    e.preventDefault();
    const barang = barangs.find(b => b.id === selectedTransaction.barangId);
    if (!barang) {
      setErrorMessage('Barang not found in inventory for validation.');
      return;
    }

    if (barang.stokBarang < selectedTransaction.quantity) {
      setErrorMessage('Stock insufficient for validation. Please request more stock from the supplier.');
      return;
    }

    onValidateTransaction(selectedTransaction.id, payment, discount);
    setSelectedTransaction(null);
    setPayment('');
    setDiscount('');
  };

  const handleRequestStock = () => {
    const barang = barangs.find(b => b.id === selectedTransaction.barangId);
    if (!barang) {
      setErrorMessage('Barang not found in inventory for stock request.');
      return;
    }

    const quantityNeeded = selectedTransaction.quantity - barang.stokBarang;
    onRequestSupplier(selectedTransaction.barangId, quantityNeeded);
    setErrorMessage('Stock request sent to supplier.');
  };

  const handleSelectBarang = (barang) => {
    setSelectedBarang(barang);
  };

  const handleAddTransaction = () => {
    if (!selectedBarang || quantity <= 0) {
      setErrorMessage('Please select a valid barang and quantity.');
      return;
    }

    const newTransaction = {
      id: transactions.length > 0 ? Math.max(...transactions.map(t => t.id)) + 1 : 1,
      itemName: selectedBarang.namaBarang,
      quantity,
      price: selectedBarang.hargaBarang,
      barangId: selectedBarang.id,
      status: 'ongoing',
    };

    onAddTransaction(newTransaction);
    setSelectedBarang(null);
    setQuantity(1);
    setErrorMessage('');
  };

  return (
    <div>
      <h2>Kasir Page</h2>
      <h3>Transactions</h3>
      <ul className="list-group">
        {transactions.map((transaction) => (
          <li
            key={transaction.id}
            className="list-group-item"
            onClick={() => handleSelectTransaction(transaction)}
          >
            {transaction.itemName} - {transaction.quantity} @ {transaction.price}
          </li>
        ))}
      </ul>
      {selectedTransaction && (
        <form onSubmit={handleValidationSubmit} className="mt-3">
          <h4>Validate Transaction</h4>
          {errorMessage && <p className="text-danger">{errorMessage}</p>}
          <div className="mb-3">
            <label htmlFor="payment" className="form-label">Payment</label>
            <input
              type="number"
              className="form-control"
              id="payment"
              value={payment}
              onChange={(e) => setPayment(e.target.value)}
              required
            />
          </div>
          <div className="mb-3">
            <label htmlFor="discount" className="form-label">Discount</label>
            <input
              type="number"
              className="form-control"
              id="discount"
              value={discount}
              onChange={(e) => setDiscount(e.target.value)}
            />
          </div>
          <button type="submit" className="btn btn-primary">Validate</button>
          {errorMessage && errorMessage.includes('insufficient') && (
            <button type="button" className="btn btn-warning" onClick={handleRequestStock}>Request Stock</button>
          )}
        </form>
      )}
      <div className="mt-5">
        <h3>Order New Barang</h3>
        <BarangList
          barangs={barangs}
          onDelete={() => {}}
          onEdit={() => {}}
          onAdd={() => {}}
          onSelect={handleSelectBarang}
        />
        {selectedBarang && (
          <div className="mt-3">
            <h4>Selected Barang: {selectedBarang.namaBarang}</h4>
            <div className="mb-3">
              <label htmlFor="quantity" className="form-label">Quantity</label>
              <input
                type="number"
                className="form-control"
                id="quantity"
                value={quantity}
                onChange={(e) => setQuantity(e.target.value)}
                min="1"
                required
              />
            </div>
            <button className="btn btn-success" onClick={handleAddTransaction}>Add Transaction</button>
          </div>
        )}
      </div>
    </div>
  );
};

KasirPage.propTypes = {
  transactions: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      itemName: PropTypes.string.isRequired,
      quantity: PropTypes.number.isRequired,
      price: PropTypes.number.isRequired,
      barangId: PropTypes.number.isRequired,
      status: PropTypes.string.isRequired,
    })
  ).isRequired,
  barangs: PropTypes.arrayOf(
    PropTypes.shape({
      id: PropTypes.number.isRequired,
      namaBarang: PropTypes.string.isRequired,
      jenisBarang: PropTypes.string.isRequired,
      hargaBarang: PropTypes.string.isRequired,
      stokBarang: PropTypes.number.isRequired,
    })
  ).isRequired,
  onValidateTransaction: PropTypes.func.isRequired,
  onRequestSupplier: PropTypes.func.isRequired,
  onAddTransaction: PropTypes.func.isRequired,
};

export default KasirPage;
