import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import BarangList from './components/barang/BarangList';
import AddBarang from './components/barang/AddBarang';
import KasirPage from './components/key/KasirPage';
import User from './components/userfd/user';
import SupplierPage from './components/key/SupplierPage';
import data from './data/barang.json';

function App() {
  const [barangs, setBarangs] = useState(data.barangs || []); // Assuming "barangs" in data.json
  const [transactions, setTransactions] = useState([]);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  // Login logic (unchanged)

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  const handleDelete = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
  };

  const handleEdit = (id) => {
    console.log(`Edit barang dengan id: ${id}`);
  };

  const handleAdd = () => {
    setCurrentPage('addBarang');
  };

  const addBarang = (barang) => {
    const newId = barangs.length > 0 ? Math.max(...barangs.map(b => b.id)) + 1 : 1;
    const newBarang = { ...barang, id: newId };
    setBarangs([...barangs, newBarang]);
    setCurrentPage('gudang');
  };

  const handleCancelAdd = () => {
    setCurrentPage('gudang');
  };

  const validateTransaction = (transactionId, payment, discount) => {
    const transaction = transactions.find(t => t.id === transactionId);
    if (transaction) {
      const barangIndex = barangs.findIndex(barang => barang.id === transaction.barangId); // Find the correct barang using its id
      if (barangIndex > -1) {
        const updatedBarangs = [...barangs];
        updatedBarangs[barangIndex].stokBarang -= transaction.quantity; // Update stock of the matching barang
        setBarangs(updatedBarangs);
      } else {
        console.log(`Barang dengan ID ${transaction.barangId} tidak ditemukan`);
      }
      setTransactions(transactions.map((t) =>
        t.id === transactionId ? { ...t, status: 'processed', payment, discount } : t
      ));
    } else {
      console.log(`Transaksi dengan ID ${transactionId} tidak ditemukan.`);
    }
  };

  // Other functions (requestSupplier, updateTransactionStatus, handleTransactionSubmit, updateStock, deleteTransaction) remain unchanged

  return (
    <div className="container mt-4">
      {!isLogin ? (
        <div>
          <h1>Login Page</h1>
          <form onSubmit={(e) => { e.preventDefault(); login(); }}>
            {/* Login form (unchanged) */}
          </form>
        </div>
      ) : (
        <div>
          <header className="mb-4">
            <nav className="navbar navbar-expand-lg navbar-light bg-light">
              <ul className="navbar-nav mr-auto">
                {/* Navigation links (unchanged) */}
              </ul>
            </nav>
          </header>

          {currentPage === 'gudang' && (
            <div>
              <h1>Gudang Page</h1>
              <BarangList barangs={barangs} onDelete={handleDelete} onEdit={handleEdit} />
              <button className="btn btn-primary mt-3" onClick={handleAdd}>Tambah Barang</button>
            </div>
          )}

          {/* Other page components (unchanged) */}

        </div>
      )}
    </div>
  );
}

export default App;
