// App.js
import React, { useState } from 'react';
import BarangList from './components/barang/BarangList';
import AddBarang from './components/barang/AddBarang';
import KasirPage from './components/key/KasirPage';
import User from './components/userfd/user'; // Impor komponen User
import data from './data/barang.json';

function App() {
  const [barangs, setBarangs] = useState(data.barangs || []);
  const [transactions, setTransactions] = useState([]);
  const [isLogin, setIsLogin] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [user, setUser] = useState('user');
  const [role, setRole] = useState('user');
  const [currentPage, setCurrentPage] = useState('user');

  const login = () => {
    if (username === "gudang" && password === "gudang123") {
      setUser(username);
      setIsLogin(true);
      setRole('gudang');
      setCurrentPage('gudang');
    } else if (username === "kasir" && password === "kasir123") {
      setUser(username);
      setIsLogin(true);
      setRole('kasir');
      setCurrentPage('kasir');
    } else if (username === "user" && password === "user123") {
      setUser(username);
      setIsLogin(true);
      setRole('user');
      setCurrentPage('user');
    } else {
      alert("Incorrect username or password");
    }
  };

  const logout = () => {
    setIsLogin(false);
    setUser('user');
    setRole('user');
    setCurrentPage('user');
  };

  const handleDelete = (id) => {
    const updatedBarangs = barangs.filter((barang) => barang.id !== id);
    setBarangs(updatedBarangs);
  };

  const handleEdit = (id) => {
    console.log(`Edit barang with id: ${id}`);
  };

  const handleAdd = () => {
    setCurrentPage('addBarang');
  };

  const addBarang = (barang) => {
    const newId = barangs.length > 0 ? Math.max(...barangs.map(b => b.id)) + 1 : 1;
    const newBarang = { ...barang, id: newId };
    setBarangs([...barangs, newBarang]);
    setCurrentPage('gudang');
  };

  const handleCancelAdd = () => {
    setCurrentPage('gudang');
  };

  const validateTransaction = (transactionId, payment, discount) => {
    setTransactions(transactions.map(t => t.id === transactionId ? { ...t, status: 'validated', payment, discount } : t));
  };

  const requestSupplier = (barangId, quantityNeeded) => {
    console.log(`Requesting ${quantityNeeded} more of barang id: ${barangId}`);
  };

  const addTransaction = (transaction) => {
    setTransactions([...transactions, { ...transaction, id: transactions.length + 1 }]);
  };

  return (
    <div className="App">
      {isLogin ? (
        <div>
          <button className="btn btn-secondary mb-3" onClick={logout}>
            Logout
          </button>
          {role === 'gudang' && currentPage === 'gudang' && (
            <BarangList
              barangs={barangs}
              onDelete={handleDelete}
              onEdit={handleEdit}
              onAdd={handleAdd}
              onLogout={logout}
            />
          )}
          {role === 'gudang' && currentPage === 'addBarang' && (
            <AddBarang
              onAdd={addBarang}
              onCancel={handleCancelAdd}
            />
          )}
          {role === 'kasir' && (
            <KasirPage
              transactions={transactions}
              barangs={barangs}
              onValidateTransaction={validateTransaction}
              onRequestSupplier={requestSupplier}
              onAddTransaction={addTransaction}
            />
          )}
          {role === 'user' && (
            <User
              barangs={barangs}
              transactions={transactions}
              handleTransactionSubmit={addTransaction}
              onLogout={logout}
            />
          )}
        </div>
      ) : (
        <div>
          <h1>Login</h1>
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />
          <button onClick={login}>Login</button>
        </div>
      )}
    </div>
  );
}

export default App;
