import React, { useState } from 'react';

const AdminPage = ({ barangs, transactions, addUser }) => {
  const [newUserUsername, setNewUserUsername] = useState('');
  const [newUserPassword, setNewUserPassword] = useState('');
  const [newUserRole, setNewUserRole] = useState('user'); // Default role

  const handleSubmit = (e) => {
    e.preventDefault();
    addUser(newUserUsername, newUserPassword, newUserRole);
    setNewUserUsername('');
    setNewUserPassword('');
    setNewUserRole('user'); // Reset role ke default
  };

  return (
    <div className="container mt-4">
      <h2 className="mb-4">Admin Page</h2>

      <div className="card">
        <div className="card-header">Add New User</div>
        <div className="card-body">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label htmlFor="username">Username:</label>
              <input 
                type="text" 
                className="form-control" 
                id="username" 
                value={newUserUsername} 
                onChange={(e) => setNewUserUsername(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="password">Password:</label>
              <input 
                type="password" 
                className="form-control" 
                id="password" 
                value={newUserPassword} 
                onChange={(e) => setNewUserPassword(e.target.value)}
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="role">Role:</label>
              <select 
                className="form-control" 
                id="role" 
                value={newUserRole} 
                onChange={(e) => setNewUserRole(e.target.value)}
              >
                <option value="user">User</option>
                <option value="gudang">Gudang</option>
                <option value="kasir">Kasir</option>
                <option value="supplier">Supplier</option>
              </select>
            </div>
            <button type="submit" className="btn btn-primary">Add User</button>
          </form>
        </div>
      </div>

      {/* ... (Tambahkan konten AdminPage lainnya seperti daftar barang, transaksi, dll.) ... */}
    </div>
  );
};

export default AdminPage;
